import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.52cc2d6a.js";const o={},n=a('<h1 id="\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1" tabindex="-1"><a class="header-anchor" href="#\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1" aria-hidden="true">#</a> \u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1</h1><p>\u4F5C\u8005\uFF1A\u5353\u522B\u6797</p><p>\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u624D\u8BA4\u8BC6\u5230\uFF0C\u6240\u6709\u7684\u75DB\u82E6\u548C\u60C5\u611F\u7684\u6298\u78E8\uFF0C</p><p>\u90FD\u53EA\u662F\u63D0\u9192\u6211\uFF1A\u6D3B\u7740\uFF0C\u4E0D\u8981\u8FDD\u80CC\u81EA\u5DF1\u7684\u672C\u5FC3\u3002</p><p>\u4ECA\u5929\u6211\u660E\u767D\u4E86\uFF0C\u8FD9\u53EB\u505A\u201C\u771F\u5B9E\u201D\u3002</p><p>\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u624D\u61C2\u5F97\uFF0C\u628A\u81EA\u5DF1\u7684\u613F\u671B\u5F3A\u52A0\u4E8E\u4EBA\uFF0C</p><p>\u662F\u591A\u4E48\u7684\u65E0\u793C\uFF0C\u5C31\u7B97\u6211\u77E5\u9053\uFF0C\u65F6\u673A\u5E76\u4E0D\u6210\u719F\uFF0C</p><p>\u90A3\u4EBA\u4E5F\u8FD8\u6CA1\u6709\u505A\u597D\u51C6\u5907\uFF0C</p><p>\u5C31\u7B97\u90A3\u4E2A\u4EBA\u5C31\u662F\u6211\u81EA\u5DF1\u3002</p><p>\u4ECA\u5929\u6211\u660E\u767D\u4E86\uFF0C\u8FD9\u53EB\u505A\u201C\u5C0A\u91CD\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u4E0D\u518D\u6E34\u6C42\u4E0D\u540C\u7684\u4EBA\u751F\uFF0C</p><p>\u6211\u77E5\u9053\u4EFB\u4F55\u53D1\u751F\u5728\u6211\u8EAB\u8FB9\u7684\u4E8B\u60C5\uFF0C</p><p>\u90FD\u662F\u5BF9\u6211\u6210\u957F\u7684\u9080\u8BF7\u3002</p><p>\u5982\u4ECA\uFF0C\u6211\u79F0\u4E4B\u4E3A\u201C\u6210\u719F\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u771F\u6B63\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u624D\u660E\u767D\uFF0C\u6211\u5176\u5B9E\u4E00\u76F4\u90FD\u5728\u6B63\u786E\u7684\u65F6\u95F4\uFF0C</p><p>\u6B63\u786E\u7684\u5730\u65B9\uFF0C\u53D1\u751F\u7684\u4E00\u5207\u90FD\u6070\u5982\u5176\u5206\u3002</p><p>\u7531\u6B64\u6211\u5F97\u4EE5\u5E73\u9759\u3002</p><p>\u4ECA\u5929\u6211\u660E\u767D\u4E86\uFF0C\u8FD9\u53EB\u505A\u201C\u81EA\u4FE1\u201D\u3002</p><p>\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u4E0D\u518D\u727A\u7272\u81EA\u5DF1\u7684\u81EA\u7531\u65F6\u95F4\uFF0C</p><p>\u4E0D\u518D\u53BB\u52FE\u753B\u4EC0\u4E48\u5B8F\u4F1F\u7684\u660E\u5929\u3002</p><p>\u4ECA\u5929\u6211\u53EA\u505A\u6709\u8DA3\u548C\u5FEB\u4E50\u7684\u4E8B\uFF0C</p><p>\u505A\u81EA\u5DF1\u70ED\u7231\u3001\u8BA9\u5FC3\u6B22\u559C\u7684\u4E8B\uFF0C</p><p>\u7528\u6211\u7684\u65B9\u5F0F\u3001\u6211\u7684\u97F5\u5F8B\u3002</p><p>\u4ECA\u5929\u6211\u660E\u767D\u4E86\uFF0C\u8FD9\u53EB\u505A\u201C\u5355\u7EAF\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u771F\u6B63\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u5F00\u59CB\u8FDC\u79BB\u4E00\u5207\u4E0D\u5065\u5EB7\u7684\u4E1C\u897F\u3002</p><p>\u4E0D\u8BBA\u662F\u996E\u98DF\u548C\u4EBA\u7269\uFF0C\u8FD8\u662F\u4E8B\u60C5\u548C\u73AF\u5883\uFF0C</p><p>\u6211\u8FDC\u79BB\u4E00\u5207\u8BA9\u6211\u8FDC\u79BB\u672C\u771F\u7684\u4E1C\u897F\u3002</p><p>\u4ECE\u524D\u6211\u628A\u8FD9\u53EB\u505A\u201C\u8FFD\u6C42\u5065\u5EB7\u7684\u81EA\u79C1\u81EA\u5229\u201D\uFF0C</p><p>\u4F46\u4ECA\u5929\u6211\u660E\u767D\u4E86\uFF0C\u8FD9\u662F\u201C\u81EA\u7231\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u771F\u6B63\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u4E0D\u518D\u603B\u60F3\u7740\u8981\u6C38\u8FDC\u6B63\u786E\uFF0C\u4E0D\u72AF\u9519\u8BEF\u3002</p><p>\u6211\u4ECA\u5929\u660E\u767D\u4E86\uFF0C\u8FD9\u53EB\u505A\u201C\u8C26\u900A\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u771F\u6B63\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u4E0D\u518D\u7EE7\u7EED\u6C89\u6EBA\u4E8E\u8FC7\u53BB\uFF0C</p><p>\u4E5F\u4E0D\u518D\u4E3A\u660E\u5929\u800C\u5FE7\u8651\uFF0C</p><p>\u73B0\u5728\u6211\u53EA\u6D3B\u5728\u4E00\u5207\u6B63\u5728\u53D1\u751F\u7684\u5F53\u4E0B\uFF0C</p><p>\u4ECA\u5929\uFF0C\u6211\u6D3B\u5728\u6B64\u65F6\u6B64\u5730\uFF0C</p><p>\u5982\u6B64\u65E5\u590D\u4E00\u65E5\u3002\u8FD9\u5C31\u53EB\u201C\u5B8C\u7F8E\u201D\u3002</p><p>\u5F53\u6211\u5F00\u59CB\u771F\u6B63\u7231\u81EA\u5DF1\uFF0C</p><p>\u6211\u660E\u767D\uFF0C\u6211\u7684\u601D\u8651\u8BA9\u6211\u53D8\u5F97\u8D2B\u4E4F\u548C\u75C5\u6001\uFF0C</p><p>\u4F46\u5F53\u6211\u5524\u8D77\u4E86\u5FC3\u7075\u7684\u529B\u91CF\uFF0C</p><p>\u7406\u667A\u5C31\u53D8\u6210\u4E86\u4E00\u4E2A\u91CD\u8981\u7684\u4F19\u4F34\uFF0C</p><p>\u8FD9\u79CD\u7EC4\u5408\u6211\u79F0\u4E4B\u4E3A\uFF0C\u201C\u5FC3\u7684\u667A\u6167\u201D\u3002</p><p>\u6211\u4EEC\u65E0\u987B\u518D\u5BB3\u6015\u81EA\u5DF1\u548C\u4ED6\u4EBA\u7684\u5206\u6B67\uFF0C</p><p>\u77DB\u76FE\u548C\u95EE\u9898\uFF0C\u56E0\u4E3A\u5373\u4F7F\u661F\u661F\u6709\u65F6\u4E5F\u4F1A\u78B0\u5728\u4E00\u8D77\uFF0C</p><p>\u5F62\u6210\u65B0\u7684\u4E16\u754C\uFF0C\u4ECA\u5929\u6211\u660E\u767D\uFF0C\u8FD9\u5C31\u662F\u201C\u751F\u547D\u201D\u3002</p><hr><h1 id="as-i-began-to-love-myself" tabindex="-1"><a class="header-anchor" href="#as-i-began-to-love-myself" aria-hidden="true">#</a> AS I BEGAN TO LOVE MYSELF</h1><p>CHARLIE CHAPLIN ON HIS 70TH BIRTHDAY</p><p>As I began to love myself I found that anguish and emotional suffering are only warning signs that I was living against my own truth.</p><p>Today, I know, this is AUTHENTICITY.</p><p>As I began to love myself I understood how much it can offend somebody as I try to force my desires on this person, even though I knew the time was not right and the person was not ready for it, and even though this person was me.</p><p>Today I call it RESPECT.</p><p>As I began to love myself I stopped craving for a different life, and I could see that everything that surrounded me was inviting me to grow.</p><p>Today I call it MATURITY.</p><p>As I began to love myself I understood that at any circumstance, I am in the right place at the right time, and everything happens at the exactly right moment, so I could be calm.</p><p>Today I call it SELF-CONFIDENCE.</p><p>As I began to love myself I quit steeling my own time, and I stopped designing huge projects for the future. Today, I only do what brings me joy and happiness, things I love to do and that make my heart cheer, and I do them in my own way and in my own rhythm.</p><p>Today I call it SIMPLICITY.</p><p>As I began to love myself I freed myself of anything that is no good for my health \u2013 food, people, things, situations, and everything that drew me down and away from myself. At first I called this attitude a healthy egoism.</p><p>Today I know it is LOVE OF ONESELF.</p><p>As I began to love myself I quit trying to always be right, and ever since I was wrong less of the time.</p><p>Today I discovered that is MODESTY.</p><p>As I began to love myself I refused to go on living in the past and worry about the future. Now, I only live for the moment, where EVERYTHING is happening.</p><p>Today I live each day, day by day, and I call it FULFILLMENT.</p><p>As I began to love myself I recognized that my mind can disturb me and it can make me sick. But As I connected it to my heart, my mind became a valuable ally.</p><p>Today I call this connection WISDOM OF THE HEART.</p><p>We no longer need to fear arguments, confrontations or any kind of problems with ourselves or others. Even stars collide, and out of their crashing new worlds are born.</p><p>Today I know THAT IS \u201CLIFE\u201C!</p>',75),s=[n];function i(r,d){return p(),t("div",null,s)}const m=e(o,[["render",i],["__file","\u5F53\u6211\u771F\u6B63\u5F00\u59CB\u7231\u81EA\u5DF1.html.vue"]]);export{m as default};
